import React from 'react';
import './sign-in.styles.scss';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import { signInWithGoogle} from '../../firebase/firebase.utils.js';

class SignIn extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            email: '',
            password: ''
        }
    }

    handleSubmit = (e) => {
        e.preventDefault();

        this.setState({email: '' , password: ''});
    }

    handleChange = (event) => {
        const { value, name} = event.target;
        this.setState({[name]: value})
    }

    render() {
        return(
         <div className = "sign-in">

            <h1> I already have an account</h1>
            <span>Sign in with your email and password</span>
            <form onSubmit = {this.handleSubmit}>

                <FormInput type="email" name = "email" value = {this.state.email}
                onChange = {this.handleChange} label = "Email"
                required/>

                <FormInput type="password" name = "password" value = {this.state.password} 
                 onChange = {this.handleChange} label = "Password"
                required/>

               <div className="buttons">
                <CustomButton htmltype="submit">  Sign in </CustomButton>
                <CustomButton htmltype = "button" onClick = {signInWithGoogle} isGoogleSignIn>  Sign in with Google </CustomButton>
               </div>


            </form>
         </div>
        )
    }

}

export default SignIn;
